options.cartocss:
/** bubble visualization */

#residential_pipeline_100416_v1_users[dcp_pipeline_status="Recently completed"] {
   marker-fill: #2E5387; 
   marker-fill-opacity: 0.7;
   marker-line-color: #FFF;
   marker-line-width: 0.5;
   marker-line-opacity: 1;
   marker-placement: point;
   marker-type: ellipse;
   marker-width: 10;
   marker-allow-overlap: true;
}

#residential_pipeline_100416_v1_users[[dcp_pipeline_status="Partial complete"] {
   marker-fill: #229A00;
     marker-fill-opacity: 0.7;
   marker-line-color: #FFF;
   marker-line-width: 0.5;
   marker-line-opacity: 1;
   marker-placement: point;
   marker-type: ellipse;
   marker-width: 10;
   marker-allow-overlap: true;
}
